ST.xhtml:
IF(Main.XPPU.Stack.Stack_WPWeight_Light) THEN
                        Crane_WPWeight_Light:=TRUE;
                        Crane_WPWeight_Medium:=FALSE;
                        Crane_WPWeight_Heavy:=FALSE;
                        ELSIF(Main.XPPU.Stack.Stack_WPWeight_Medium)THEN
                        Crane_WPWeight_Light:=FALSE;
                        Crane_WPWeight_Medium:=TRUE;
                        Crane_WPWeight_Heavy:=FALSE;
                        ELSIF(Main.XPPU.Stack.Stack_WPWeight_Heavy)THEN
                        Crane_WPWeight_Light:=FALSE;
                        Crane_WPWeight_Medium:=FALSE;
                        Crane_WPWeight_Heavy:=TRUE;
                        END_IF

Crane_WPPickedUp:=FALSE;
                        AtStack:=FALSE;
                        AtConveyor:=FALSE;
                        AtStamp:=FALSE;

                        CraneTurnClockwise:=FALSE;
                        CraneTurnCounterclockwise:=FALSE;
                        Gripper.ACT_Release();

InterfaceAsPlainText.xhtml:
FUNCTION_BLOCK Crane
                    VAR_INPUT
                    LiftingCylinder : MonostableCylinder;
                    Gripper : VacuumGripper;
                    //Software
                    Crane_SoftwareEmergency : BOOL;
                    SFCReset : BOOL;
                    InitCompleted : BOOL;
                    LSC_AllowedToOperate : BOOL;
                    Stamp_AllowedToOperate : BOOL;
                    EmergencyHandeled : BOOL;
                    END_VAR
                    VAR_OUTPUT
                    //Software
                    Crane_WPPickedUp : BOOL;
                    Crane_WPWeight_Light : BOOL;
                    Crane_WPWeight_Medium : BOOL;
                    Crane_WPWeight_Heavy : BOOL;
                    AtStack : BOOL := FALSE;
                    AtConveyor : BOOL := FALSE;
                    AtStamp : BOOL := FALSE;
                    END_VAR
                    VAR
                    //Hardware
                    Crane_OnStack AT %I* : BOOL;
                    Crane_OnConveyor AT %I* : BOOL;
                    Crane_OnStamp AT %I* : BOOL;
                    CraneTurnClockwise AT %Q* : BOOL;
                    CraneTurnCounterclockwise AT %Q* : BOOL;

                    //Software
                    Timer : TON;
                    AutomaticExecuteAktive : BOOL;
                    END_VAR
